const test = require('tap').test
    , prr  = require('./')

test('test prr(o, key, value) form', function (t) {
  t.plan(2)

  var o = {}
  prr(o, 'foo', 'bar')
  t.equal(o.foo, 'bar', 'correct value')
  t.deepEqual(
      Object.getOwnPropertyDescriptor(o, 'foo')
    , {
          enumerable   : false
        , configurable : false
        , writable     : false
        , value        : 'bar'
      }
    , 'correct property descriptor'
  )
  t.end()
})

